            Status = "active",
            Port = 80,
            ProxyProtocolVersion = v2,
            Weight = 2,
            Probe = true,
            Ssl = false,
            Backup = true,
        });
        var mylb = new Ovh.IPLoadBalancingRefresh("mylb", new Ovh.IPLoadBalancingRefreshArgs
        {
            ServiceName = lb.Apply(lb => lb.Id),
            Keepers = 
            {

                {
                    backend,
                }.Select(__item => __item.Address).ToList(),
            },
        });
    }

}
