            {
                Duration = vrackProductCartProductPlan.Apply(vrackProductCartProductPlan => vrackProductCartProductPlan.SelectedPrices?[0]?.Duration),
                PlanCode = vrackProductCartProductPlan.Apply(vrackProductCartProductPlan => vrackProductCartProductPlan.PlanCode),
                PricingMode = vrackProductCartProductPlan.Apply(vrackProductCartProductPlan => vrackProductCartProductPlan.SelectedPrices?[0]?.PricingMode),
            },
